mapper.insert:
insert into infrMember (
	ifmmId
	, ifmmName
	, ifmmDelNy 
	, ifmmPassword
	, ifmmPhoneNumber1
	, ifmmPhoneNumber2
	, ifmmPhoneNumber3
	, ifmmAreaCodeCd
	, ifmmNumber1
	, ifmmNumber2 
	, ifmmDob
	, ifmmZipCode
	, ifmmAddress
	, ifmmDetailedAddress 
) values (
	#{ifmmId}
	, #{ifmmName}
	, 0
	, #{ifmmPassword}
	, #{ifmmPhoneNumber1}
	, #{ifmmPhoneNumber2}
	, #{ifmmPhoneNumber3}
	, #{ifmmAreaCodeCd}
	, #{ifmmNumber1}
	, #{ifmmNumber2}
	, #{ifmmDob}
	, #{ifmmZipCode}
	, #{ifmmAddress}
	, #{ifmmDetailedAddress}
)

mapper.update:
UPDATE infrMember set
	ifmmId = #{ifmmId}
	, ifmmName = #{ifmmName}
	, ifmmPassword = #{ifmmPassword}
	, ifmmPhoneNumber1 = #{ifmmPhoneNumber1}
    , ifmmPhoneNumber2 = #{ifmmPhoneNumber2}
    , ifmmPhoneNumber3 = #{ifmmPhoneNumber3}
    , ifmmNumber1 = #{ifmmNumber1}
    , ifmmNumber2 = #{ifmmNumber2}
    , ifmmDob = #{ifmmDob}
    , ifmmZipCode = #{ifmmZipCode}
    , ifmmAddress = #{ifmmAddress}
    , ifmmDetailedAddress = #{ifmmDetailedAddress}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
